package liga;

public class Main {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub

		// Declarar variables
				int numero1 = 10;
				int numero2 = 3;
				
				// Variables de resultado
				int resultadoSuma = 0;
				int resultadoResta = 0;
				int resultadoDivision = 0;
				int resultadoMultiplicacion = 0;
				int resultadoMod = 0;
				
				// Procesamiento
				resultadoSuma = numero1 + numero2; 
				resultadoResta = numero1 - numero2;
				resultadoDivision = numero1 / numero2;
				resultadoMultiplicacion = numero1 * numero2;
				resultadoMod = numero1 % numero2;
				
				
				// TODO Auto-generated method stub
				System.out.println("El resultado de la suma es: " + resultadoSuma);
				System.out.println("El resultado de la suma es: " + resultadoResta);
				System.out.println("El resultado de la suma es: " + resultadoDivision);
				System.out.println("El resultado de la suma es: " + resultadoMultiplicacion);
				System.out.println("El resultado de la suma es: " + resultadoMod);
				
				
		
	}

}
